import React, {useState, useEffect} from 'react'
import './App.css';



function App() {

  const [endPoint, setEndPoints] = useState('');
  const [container, setContainer] = useState([]);
  const [finalPoint, setFinalPoint] = useState("");

  useEffect(()=> {
    fetchMe()

  },[finalPoint]) // eslint-disable-line react-hooks/exhaustive-deps

  const fetchMe =  () => {

    fetch(`'https://imdb8.p.rapidapi.com/auto-complete?q=+${endPoint}`, {
      "method": 'GET',
      "headers": {
        'X-RapidAPI-Key': '65e845c99fmshfb7070778d6ea7fp1c7901jsn4ba7a127ff3f',
        'X-RapidAPI-Host': 'imdb-com.p.rapidapi.com'
      }
    })

    .then(response => response.json())
      .then(data => {
        if (data?.d) {
          setContainer(data.d);
        } else {
          setContainer([]);
        }
      })
      .catch(err => {
        console.error(err);
        setContainer([]);
      });
  };

const onChangeHandler = (e) =>{
  setEndPoints(e.target.value)
}

const submitHandler = e =>{
  e.preventDefault()
  setFinalPoint(endPoint)
}

  return (
    <div className="App">

    <form onSubmit={submitHandler}>

      <input type="text" value={endPoint} onChange={onChangeHandler}/>
      <button type="submit"> Submit </button>

    </form>
    {container && container.length > 0 && container.map(item => (
        <div key={item.id}>
          <img src={item.i?.imageUrl} alt="" />
          <p>{item.l}</p>
          </div>
        )
      )}

    </div>
  )
}

export default App;
