import React, {useState, useEffect} from 'react'
import './App.css';



function App() {

  const [endPoint, setEndPoints] = useState('')
  const [container, setContainer] = useState([])
  const [finalPoint, setFinalPoint] = useState("")

  useEffect(()=> {
    fetchMe()

  },[finalPoint]) // eslint-disable-line react-hooks/exhaustive-deps

  const fetchMe =  () => {

    fetch(`https://imdb-com.p.rapidapi.com/auto-complete?q=+${endPoint}`, {
      "method": 'GET',
      "headers": {
        'X-RapidAPI-Key': '65e845c99fmshfb7070778d6ea7fp1c7901jsn4ba7a127ff3f',
        'X-RapidAPI-Host': 'imdb-com.p.rapidapi.com'
      }
    })

    .then(response => {
      return response.json();
    })
    .then(data =>{
      setContainer(data.d);
    })
    .catch(err =>{
      console.error(err);
    });
    }


const onChangeHandler = (e) =>{
  setEndPoints(e.target.value)
}

const submitHandler = e =>{
  e.preventDefault()
  setFinalPoint(endPoint)
}

  return (
    <div className="App">

    <form onSubmit={submitHandler}>

      <input type="text" value={endPoint} onChange={onChangeHandler}/>
      <button type="submit"> Submit </button>

    </form>

      {container.map((item)=> {
        return (
          <div>
          <img src={item.i.imageUrl} alt="" />
          <p>{item.v}</p>
          </div>
        )
      })}

    </div>
  )
}

export default App;
